let buyButtons=document.querySelectorAll(".perforator-item a"),cartInfoWindow=document.querySelector(".good-info-cart"),cartInfoCloseButon=cartInfoWindow.querySelector(".close-button"),cartInfoSpan=document.querySelector(".cart span"),goodInCartNumber=cartInfoSpan.innerHTML,continueShoppingButton=cartInfoWindow.querySelector(".continue-shopping");for(button of buyButtons)button.addEventListener("click",function(a){a.preventDefault(),goodInCartNumber++,cartInfoSpan.innerHTML=goodInCartNumber,0<goodInCartNumber&&cartInfoSpan.parentElement.classList.add("goods-inside"),cartInfoWindow.classList.remove("hidden-window")});cartInfoCloseButon.addEventListener("click",function(){cartInfoWindow.classList.add("hidden-window")}),continueShoppingButton.addEventListener("click",function(){cartInfoWindow.classList.add("hidden-window")}),window.addEventListener("keydown",function(a){27!==a.keyCode||cartInfoWindow.classList.contains("hidden-window")||(a.preventDefault(),cartInfoWindow.classList.add("hidden-window"))});