let deliveryButton=document.querySelector(".delivery-service-button"),quaranteeButton=document.querySelector(".quarantee-service-button"),creditButton=document.querySelector(".credit-service-button"),servicesDivs=document.querySelectorAll(".service");deliveryButton.addEventListener("click",function(){deliveryButton.classList.add("selected-service"),quaranteeButton.classList.remove("selected-service"),creditButton.classList.remove("selected-service"),showDivWithClass("delivery")}),quaranteeButton.addEventListener("click",function(){deliveryButton.classList.remove("selected-service"),quaranteeButton.classList.add("selected-service"),creditButton.classList.remove("selected-service"),showDivWithClass("quarantee")}),creditButton.addEventListener("click",function(){deliveryButton.classList.remove("selected-service"),quaranteeButton.classList.remove("selected-service"),creditButton.classList.add("selected-service"),showDivWithClass("credit")});function showDivWithClass(a){for(serviceDiv of servicesDivs)serviceDiv.classList.contains(a)?removeDivClass(serviceDiv,"hidden-service"):setDivClass(serviceDiv,"hidden-service")}function removeDivClass(a,b){a.classList.remove(b)}function setDivClass(a,b){a.classList.add(b)}